mapper.select:
SELECT a.id,u1.name as `owner`,a.name,a.start_date,a.end_date,a.cost,a.description,u2.`name` as create_by,a.create_time, u3.`name` as edit_by,a.edit_time from tbl_activity a join tbl_user u1 on a.`owner`=u1.id join tbl_user u2 on a.create_by=u2.id left join tbl_user u3 on a.edit_by=u3.id
and a.name like '%' #{name} '%'
and u1.name like '%' #{owner} '%'
and a.start_date>=#{startDate}
and a.end_date<=#{endDate}
ORDER BY a.create_time desc limit #{beginNo},#{pageSize}

select count(*) as totalRows from tbl_activity a join tbl_user u1 on a.owner=u1.id join tbl_user u2 on a.create_by=u2.id left join tbl_user u3 on a.edit_by=u3.id
and a.name like '%' #{name} '%'
and u1.name like '%' #{owner} '%'
and a.start_date>=#{startDate}
and a.end_date<=#{endDate}
